import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Link } from 'react-router-dom';
import axios from 'axios';
import '../App.css';

export default function GetList(props) {
  const [todosArr, setTodosArr] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:4000/todo')
      .then(res => setTodosArr(res.data))
      .catch(err => console.log(err));
  });

  const handleDelete = () => {
    props.history.push(`/todo/delete/${item._id}`);
    console.log('id in deleted task func is', props.match.params.id);
    axios
      .delete('http://localhost:4000/todo/delete/' + props.match.params.id)
      .then(res => console.log('this is response of axios.delete', res))
      .catch(err => console.log('this is error from axios.delete', err));
    console.log('item is deleted u know');
    props.history.push('/todo');
  };

  return (
    <div>
      <h3>
        <u>The list of items is :</u>
      </h3>
      <table border='1'>
        {todosArr.map(item => {
          return (
            <tr>
              <td>{item.task}</td>
              <td>
                <Router>
                  <Link to={`/edit/${item._id}`}>Edit</Link>
                </Router>
              </td>
              <td>
                <Router>
                  <Link to={`/todo/delete/${item._id}`} onClick={handleDelete}>
                    Delete
                  </Link>
                </Router>
              </td>
            </tr>
          );
        })}
      </table>
    </div>
  );
}
